public class Rectangle { public Point topLeft; public double height; public double width; }

public class Circle { public Point center; public double radius; }

public class Geometry { public final double PI = 3.141592653589793;

public double area(Object shape) throws NoSuchShapeException { if (shape instanceof Square) { Square s = (Square)shape; return s.side * s.side; }

